<?php

namespace AppBundle\Entity\Congres;

use Doctrine\ORM\EntityRepository;
use AppBundle\Entity\Instance;
/**
 * GeneralContributionRepository
 *
 * This class was generated by the Doctrine ORM. Add your own custom
 * repository methods below.
 */
class GeneralContributionRepository extends EntityRepository
{

    public function getCNVotesCount($contrib)
    {
        $CNCount = $this->createQueryBuilder('gc')
            ->select('COUNT(gc)')
            ->innerJoin('gc.votes', 'u')
            ->innerJoin('u.profile', 'adh')
            ->innerJoin('adh.instances', 'i')
            ->where('gc.id = :contrib')
            ->andWhere('i.name = :iname')
            ->setParameter('contrib', $contrib->getId())
            ->setParameter('iname', Instance::INSTANCE_CN)
            ->getQuery()->getSingleScalarResult();
        return $CNCount;

    }

    public function getVotesCount($contrib)
    {
        $VoteCount = $this->createQueryBuilder('gc')
            ->select('COUNT(gc)')
            ->innerJoin('gc.votes', 'u')
            ->where('gc.id = :contrib')
            ->setParameter('contrib', $contrib->getId())
            ->getQuery()->getSingleScalarResult();
        return $VoteCount;
    }
    public function hasVoted($user)
    {
        $hasVoted = $this->createQueryBuilder('gc')
            ->select('COUNT(gc)')
            ->innerJoin('gc.votes', 'u')
            ->where('u.id = :user')
            ->setParameter('user', $user->getId())
            ->getQuery()->getSingleScalarResult();

        return $hasVoted;
    }

    public function hasAlreadyVoted($contrib, $user)
    {
        $hasAlreadyVoted = $this->createQueryBuilder('gc')
            ->select('COUNT(gc)')
            ->innerJoin('gc.votes', 'u')
            ->where('u.id = :user')
            ->andWhere('gc.id = :id')
            ->setParameter('user', $user->getId())
            ->setParameter('id', $contrib->getId())
            ->getQuery()->getSingleScalarResult();
        return $hasAlreadyVoted;
    }
}
